document.addEventListener('DOMContentLoaded', () => {
  const analysisForm = document.getElementById('analysis-form');
  const resultsSection = document.getElementById('results-section');
  const statusMessage = document.getElementById('status-message');
  const loadingIndicator = document.getElementById('loading-indicator');
  const tokenLimit = document.getElementById('token-limit');
  const tokenDisplay = document.getElementById('token-display');

  // Update token display when slider changes
  tokenLimit.addEventListener('input', () => {
    tokenDisplay.textContent = `${Number(tokenLimit.value).toLocaleString()} tokens`;
  });

  // Handle form submission
  analysisForm.addEventListener('submit', async (e) => {
    e.preventDefault();

    const youtubeUrl = document.getElementById('youtube-url').value;
    const aiModel = document.getElementById('ai-model').value;
    const promptTemplate = document.getElementById('prompt-template').value;
    const tokenLimitValue = tokenLimit.value;

    // Show loading state
    showStatus('Analyzing video...', true);
    resultsSection.classList.add('hidden');

    try {
      // Call API to analyze video
      const response = await fetch('/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          url: youtubeUrl,
          model: aiModel,
          promptTemplate: promptTemplate,
          tokenLimit: tokenLimitValue
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to analyze video');
      }

      const data = await response.json();
      displayResults(data);
      showStatus('Analysis complete!', false);
      resultsSection.classList.remove('hidden');

    } catch (error) {
      showStatus(`Error: ${error.message}`, false, true);
    }
  });

  // Display analysis results
  function displayResults(data) {
    const videoInfo = document.getElementById('video-info');
    const analysisResults = document.getElementById('analysis-results');

    // Display video metadata
    videoInfo.innerHTML = `
      <div class="video-header">
        <img src="${data.videoInfo.thumbnail}" alt="${data.videoInfo.title}">
        <div class="video-details">
          <h2>${data.videoInfo.title}</h2>
          <p>Channel: ${data.videoInfo.channel}</p>
          <p>Published: ${new Date(data.videoInfo.publishedAt).toLocaleDateString()}</p>
          <p>Views: ${Number(data.videoInfo.viewCount).toLocaleString()}</p>
        </div>
      </div>
    `;

    // Display analysis
    analysisResults.innerHTML = `<div class="markdown-content">${markdownToHtml(data.analysis)}</div>`;

    // Setup download buttons
    document.getElementById('download-md').onclick = () => downloadFile(data.analysis, `${sanitizeFilename(data.videoInfo.title)}-analysis.md`, 'text/markdown');

    if (data.transcript) {
      document.getElementById('download-transcript').classList.remove('hidden');
      document.getElementById('download-transcript').onclick = () => downloadFile(data.transcript, `${sanitizeFilename(data.videoInfo.title)}-transcript.txt`, 'text/plain');
    } else {
      document.getElementById('download-transcript').classList.add('hidden');
    }
  }

  // Helper functions
  function showStatus(message, isLoading = false, isError = false) {
    statusMessage.textContent = message;
    statusMessage.className = isError ? 'error' : '';
    loadingIndicator.classList.toggle('hidden', !isLoading);
  }

  function sanitizeFilename(name) {
    return name.replace(/[^a-z0-9]/gi, '_').toLowerCase();
  }

  function downloadFile(content, filename, contentType) {
    const blob = new Blob([content], { type: contentType });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  }

  function markdownToHtml(markdown) {
    // Simple markdown to HTML conversion
    // In a real implementation, use a proper markdown parser
    return markdown
      .replace(/^### (.*$)/gim, '<h3>$1</h3>')
      .replace(/^## (.*$)/gim, '<h2>$1</h2>')
      .replace(/^# (.*$)/gim, '<h1>$1</h1>')
      .replace(/\*\*(.*?)\*\*/gim, '<strong>$1</strong>')
      .replace(/\*(.*?)\*/gim, '<em>$1</em>')
      .replace(/\n/gim, '<br>');
  }
});
