const { YoutubeTranscript } = require('youtube-transcript');
const { getSubtitles } = require('youtube-captions-scraper');
const { getVideoMetadata } = require('./youtube');

async function getTranscript(videoId) {
  // Try multiple methods in sequence
  try {
    // Method 1: youtube-transcript package
    return await getTranscriptMethod1(videoId);
  } catch (error1) {
    console.log('Method 1 failed, trying method 2...');
    try {
      // Method 2: youtube-captions-scraper
      return await getTranscriptMethod2(videoId);
    } catch (error2) {
      console.log('Method 2 failed, falling back to description...');
      // Fallback: Use video description
      return await getDescriptionFallback(videoId);
    }
  }
}

async function getTranscriptMethod1(videoId) {
  const transcript = await YoutubeTranscript.fetchTranscript(videoId);
  if (!transcript || transcript.length === 0) {
    throw new Error('No transcript found with method 1');
  }

  // Format transcript
  return formatTranscript(transcript);
}

async function getTranscriptMethod2(videoId) {
  const captions = await getSubtitles({
    videoID: videoId,
    lang: 'en' // Try English first
  });

  if (!captions || captions.length === 0) {
    // Try auto-generated captions
    const autoCaptions = await getSubtitles({
      videoID: videoId,
      lang: 'en', 
      auto: true
    });

    if (!autoCaptions || autoCaptions.length === 0) {
      throw new Error('No transcript found with method 2');
    }

    return formatCaptions(autoCaptions);
  }

  return formatCaptions(captions);
}

async function getDescriptionFallback(videoId) {
  const metadata = await getVideoMetadata(videoId);
  return {
    text: `[No transcript available] Video description:\n\n${metadata.description}`,
    hasTranscript: false
  };
}

function formatTranscript(transcript) {
  const formattedText = transcript
    .map(item => `[${formatTimestamp(item.offset)}] ${item.text}`)
    .join('\n');

  return {
    text: formattedText,
    hasTranscript: true,
    segments: transcript
  };
}

function formatCaptions(captions) {
  const formattedText = captions
    .map(item => `[${item.start}] ${item.text}`)
    .join('\n');

  return {
    text: formattedText,
    hasTranscript: true,
    segments: captions
  };
}

function formatTimestamp(milliseconds) {
  const seconds = Math.floor(milliseconds / 1000);
  const minutes = Math.floor(seconds / 60);
  const remainingSeconds = seconds % 60;
  return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
}

module.exports = {
  getTranscript
};
```
