1. Create AI service in `/services/ai.js`:
```javascript
const axios = require('axios');

async function analyzeContent(content, options) {
  const {
    model = 'gpt-3.5-turbo',
    promptTemplate,
    tokenLimit = 6000,
    videoInfo
  } = options;

  try {
    // Prepare the prompt with video metadata and transcript
    const prompt = preparePrompt(promptTemplate, videoInfo, content, tokenLimit);

    // Call OpenRouter API
    const response = await axios.post(
      'https://openrouter.ai/api/v1/chat/completions',
      {
        model: model,
        messages: [
          { role: 'system', content: 'You are a helpful assistant that analyzes YouTube videos.' },
          { role: 'user', content: prompt }
        ],
        max_tokens: Math.min(tokenLimit, getModelMaxTokens(model))
      },
      {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.OPENROUTER_API_KEY}`,
          'HTTP-Referer': process.env.APP_URL || 'http://localhost:3000',
          'X-Title': 'YouTube Video Research App'
        }
      }
    );

    return {
      analysis: response.data.choices[0].message.content,
      modelUsed: model,
      promptUsed: promptTemplate,
      tokenLimit: tokenLimit,
      promptText: prompt
    };
  } catch (error) {
    console.error('OpenRouter API error:', error.response?.data || error.message);
    throw new Error(`AI analysis failed: ${error.response?.data?.error?.message || error.message}`);
  }
}

function preparePrompt(templateName, videoInfo, transcript, tokenLimit) {
  // Load the template
  const template = getPromptTemplate(templateName);

  // Replace placeholders with actual data
  let prompt = template
    .replace('{{VIDEO_TITLE}}', videoInfo.title)
    .replace('{{CHANNEL_NAME}}', videoInfo.channel)
    .replace('{{PUBLISH_DATE}}', new Date(videoInfo.publishedAt).toLocaleDateString())
    .replace('{{VIEW_COUNT}}', videoInfo.viewCount)
    .replace('{{LIKE_COUNT}}', videoInfo.likeCount || 'N/A')
    .replace('{{VIDEO_DESCRIPTION}}', videoInfo.description);

  // Add transcript with token limit consideration
  const estimatedPromptTokens = prompt.length / 4; // Rough estimate
  const availableTokens = tokenLimit - estimatedPromptTokens - 500; // Reserve tokens for response

  if (transcript.hasTranscript) {
    // Truncate transcript if needed
    const transcriptText = transcript.text;
    const estimatedTranscriptTokens = transcriptText.length / 4;

    if (estimatedTranscriptTokens > availableTokens) {
      // Truncate transcript to fit token limit
      const truncatedText = transcriptText.substring(0, Math.floor(availableTokens * 4));
      prompt = prompt.replace('{{TRANSCRIPT}}', truncatedText + '\n[Transcript truncated due to length]');
    } else {
      prompt = prompt.replace('{{TRANSCRIPT}}', transcriptText);
    }
  } else {
    prompt = prompt.replace('{{TRANSCRIPT}}', '[No transcript available]\n' + transcript.text);
  }

  return prompt;
}

function getPromptTemplate(templateName) {
  // In a real implementation, these would be loaded from files
  const templates = {
    'general': `# YouTube Video Analysis Request\n\nPlease analyze the following YouTube video:\n\n## Video Information\n- Title: {{VIDEO_TITLE}}\n- Channel: {{CHANNEL_NAME}}\n- Published: {{PUBLISH_DATE}}\n- Views: {{VIEW_COUNT}}\n- Likes: {{LIKE_COUNT}}\n\n## Video Description\n{{VIDEO_DESCRIPTION}}\n\n## Transcript\n{{TRANSCRIPT}}\n\n## Analysis Instructions\nPlease provide a comprehensive analysis of this video including:\n1. A concise summary of the main points\n2. Key insights and takeaways\n3. Structure and content organization\n4. Target audience analysis\n5. Quality assessment\n\nFormat your response in markdown with clear headings and bullet points.`,
    'tutorial': `# YouTube Tutorial Analysis Request\n\nPlease analyze the following tutorial video:\n\n## Video Information\n- Title: {{VIDEO_TITLE}}\n- Channel: {{CHANNEL_NAME}}\n- Published: {{PUBLISH_DATE}}\n- Views: {{VIEW_COUNT}}\n- Likes: {{LIKE_COUNT}}\n\n## Video Description\n{{VIDEO_DESCRIPTION}}\n\n## Transcript\n{{TRANSCRIPT}}\n\n## Analysis Instructions\nPlease provide a comprehensive analysis of this tutorial including:\n1. A step-by-step breakdown of the tutorial process\n2. Required tools, materials, or prerequisites\n3. Key techniques or methods taught\n4. Common pitfalls or challenges mentioned\n5. Alternative approaches suggested\n\nFormat your response in markdown with clear headings, numbered steps, and code blocks where appropriate.`,
    'review': `# YouTube Product Review Analysis Request\n\nPlease analyze the following product review video:\n\n## Video Information\n- Title: {{VIDEO_TITLE}}\n- Channel: {{CHANNEL_NAME}}\n- Published: {{PUBLISH_DATE}}\n- Views: {{VIEW_COUNT}}\n- Likes: {{LIKE_COUNT}}\n\n## Video Description\n{{VIDEO_DESCRIPTION}}\n\n## Transcript\n{{TRANSCRIPT}}\n\n## Analysis Instructions\nPlease provide a comprehensive analysis of this product review including:\n1. Product specifications and features discussed\n2. Pros and cons mentioned by the reviewer\n3. Comparisons to competing products\n4. Overall verdict and rating\n5. User cases and recommendations\n\nFormat your response in markdown with clear headings, tables for comparisons, and bullet points for pros/cons.`
  };

  return templates[templateName] || templates['general'];
}

function getModelMaxTokens(model) {
  // Maximum context lengths for different models
  const modelLimits = {
    'gpt-3.5-turbo': 16385,
    'gpt-4': 8192,
    'claude-2': 100000,
    'anthropic/claude-2': 100000
  };

  return modelLimits[model] || 8000; // Default fallback
}

module.exports = {
  analyzeContent
