const express = require('express');
const router = express.Router();
const { extractVideoId, getVideoMetadata } = require('../services/youtube');
const { getTranscript } = require('../services/transcript');
const { analyzeContent } = require('../services/ai');

router.post('/', async (req, res) => {
  try {
    const { url, model = 'gpt-3.5-turbo', promptTemplate = 'general', tokenLimit = 6000 } = req.body;

    if (!url) {
      return res.status(400).json({ message: 'YouTube URL is required' });
    }

    // Extract video ID from URL
    const videoId = extractVideoId(url);

    // Get video metadata
    const videoInfo = await getVideoMetadata(videoId);

    // Get transcript
    const transcript = await getTranscript(videoId);

    // Analyze content with AI
    const analysisResult = await analyzeContent(transcript, {
      model,
      promptTemplate,
      tokenLimit: parseInt(tokenLimit),
      videoInfo
    });

    // Return combined result
    res.json({
      videoInfo,
      analysis: analysisResult.analysis,
      transcript: transcript.text,
      hasTranscript: transcript.hasTranscript,
      modelUsed: analysisResult.modelUsed,
      promptUsed: analysisResult.promptUsed,
      tokenLimit: analysisResult.tokenLimit
    });
  } catch (error) {
    console.error('Analysis error:', error);
    res.status(500).json({ message: error.message });
  }
});

module.exports = router;
