```javascript
const fs = require('fs').promises;
const path = require('path');

let promptCache = {};
let lastCacheUpdate = 0;
const CACHE_TTL = 60 * 1000; // 1 minute

async function loadPrompts() {
  const now = Date.now();

  // Return cached prompts if still valid
  if (Object.keys(promptCache).length > 0 && now - lastCacheUpdate < CACHE_TTL) {
    return promptCache;
  }

  const prompts = {};

  // Load default prompts
  const defaultPromptsDir = path.join(__dirname, '../prompts/default');
  const defaultPromptFiles = await fs.readdir(defaultPromptsDir);

  for (const file of defaultPromptFiles) {
    if (file.endsWith('.md')) {
      const name = file.replace('.md', '');
      const content = await fs.readFile(path.join(defaultPromptsDir, file), 'utf8');
      prompts[name] = {
        name,
        content,
        isDefault: true
      };
    }
  }

  // Load custom prompts
  try {
    const customPromptsDir = path.join(__dirname, '../prompts/custom');
    const customPromptFiles = await fs.readdir(customPromptsDir);

    for (const file of customPromptFiles) {
      if (file.endsWith('.md')) {
        const name = file.replace('.md', '');
        const content = await fs.readFile(path.join(customPromptsDir, file), 'utf8');
        prompts[name] = {
          name,
          content,
          isDefault: false
        };
      }
    }
  } catch (error) {
    // Create custom directory if it doesn't exist
    if (error.code === 'ENOENT') {
      await fs.mkdir(path.join(__dirname, '../prompts/custom'), { recursive: true });
    } else {
      console.error('Error loading custom prompts:', error);
    }
  }

  // Update cache
  promptCache = prompts;
  lastCacheUpdate = now;

  return prompts;
}

async function getPromptContent(promptName) {
  const prompts = await loadPrompts();
  return prompts[promptName]?.content || prompts['general'].content;
}

async function addCustomPrompt(name, content) {
  const sanitizedName = name.replace(/[^a-z0-9-_]/gi, '');
  const promptPath = path.join(__dirname, '../prompts/custom', `${sanitizedName}.md`);

  await fs.writeFile(promptPath, content, 'utf8');

  // Invalidate cache
  lastCacheUpdate = 0;

  return sanitizedName;
}

async function getAvailablePrompts() {
  const prompts = await loadPrompts();
  return Object.values(prompts).map(prompt => ({
    name: prompt.name,
    isDefault: prompt.isDefault
  }));
}

module.exports = {
  getPromptContent,
  addCustomPrompt,
  getAvailablePrompts
};
