const fs = require('fs');
const path = require('path');
const os = require('os');
const crypto = require('crypto');

// Create temporary directory for downloads
const tempDir = path.join(os.tmpdir(), 'youtube-research-app');
if (!fs.existsSync(tempDir)) {
  fs.mkdirSync(tempDir, { recursive: true });
}

// Clean up old files periodically
setInterval(() => {
  const now = Date.now();
  fs.readdir(tempDir, (err, files) => {
    if (err) return;

    files.forEach(file => {
      const filePath = path.join(tempDir, file);
      fs.stat(filePath, (err, stats) => {
        if (err) return;

        // Remove files older than 1 hour
        if (now - stats.mtime.getTime() > 60 * 60 * 1000) {
          fs.unlink(filePath, () => {});
        }
      });
    });
  });
}, 30 * 60 * 1000); // Run every 30 minutes

function sanitizeFilename(name) {
  return name
    .replace(/[^a-z0-9]/gi, '_')
    .toLowerCase()
    .substring(0, 100); // Limit length
}

async function createDownloadFile(content, filename, contentType) {
  // Generate unique ID for the file
  const fileId = crypto.randomBytes(16).toString('hex');
  const sanitizedName = sanitizeFilename(filename);
  const fullFilename = `${sanitizedName}-${fileId}`;
  const filePath = path.join(tempDir, fullFilename);

  // Write content to file
  fs.writeFileSync(filePath, content);

  return {
    id: fileId,
    filename: sanitizedName,
    path: filePath,
    contentType
  };
}

