async function getTranscriptForTrack(videoId, languageCode) {
  try {
    const { getSubtitles } = require('youtube-captions-scraper');
    const captions = await getSubtitles({
      videoID: videoId,
      lang: languageCode
    });

    if (!captions || captions.length === 0) {
      throw new Error(`No captions found for language: ${languageCode}`);
    }

    // Detect language to confirm
    const text = captions.map(c => c.text).join(' ');
    const detectedLanguage = detectLanguage(text);

    return {
      text: formatCaptions(captions),
      hasTranscript: true,
      segments: captions,
      language: {
        code: languageCode,
        name: ISO6391.getName(languageCode.split('-')[0]),
        detected: detectedLanguage
      }
    };
  } catch (error) {
    console.error(`Error getting transcript for language ${languageCode}:`, error);
