function detectLanguage(text) {
  // Only detect if we have enough text
  if (text.length < 50) return null;

  try {
    const detections = lngDetector.detect(text, 2);
    if (detections && detections.length > 0) {
      return {
        code: detections[0][0],
        confidence: detections[0][1]
      };
    }
    return null;
  } catch (error) {
    console.error('Language detection error:', error);
    return null;
  }
}

function formatCaptions(captions) {
  // Group captions by speaker if possible
  let formattedText = '';
  let currentSpeaker = null;
  let currentParagraph = '';

  for (const caption of captions) {
    const text = caption.text;
    const timestamp = formatTimestamp(caption.start);

    // Check if this might be a new speaker
    const speakerMatch = text.match(/^([A-Z][a-z]+:)\s(.+)$/);

    if (speakerMatch) {
      // New speaker detected
      if (currentParagraph) {
        formattedText += currentParagraph + '\n\n';
        currentParagraph = '';
      }

      currentSpeaker = speakerMatch[1];
      currentParagraph = `[${timestamp}] ${currentSpeaker} ${speakerMatch[2]}`;
    } else if (text.startsWith('- ') || text.startsWith(' ')) {
      // List item
      if (currentParagraph) {
        formattedText += currentParagraph + '\n\n';
        currentParagraph = '';
      }
      formattedText += `[${timestamp}] ${text}\n`;
    } else {
      // Continue current paragraph or start new one
      if (!currentParagraph) {
        currentParagraph = `[${timestamp}] ${text}`;
      } else {
        // Check if this is likely a continuation
        if (text.length < 30 || !text.match(/^[A-Z]/) || !currentParagraph.endsWith('.')) {
          currentParagraph += ' ' + text;
        } else {
          formattedText += currentParagraph + '\n\n';
          currentParagraph = `[${timestamp}] ${text}`;
        }
      }
    }
  }

  // Add the last paragraph
  if (currentParagraph) {
    formattedText += currentParagraph;
  }

  return formattedText;
}
