const { ApifyClient } = require('apify-client');

let apifyClient = null;

function initializeApifyClient() {
  if (!process.env.APIFY_API_TOKEN) {
    throw new Error('Apify API token not configured');
  }

  apifyClient = new ApifyClient({
    token: process.env.APIFY_API_TOKEN,
  });

  return apifyClient;
}

async function getTranscriptViaApify(videoId) {
  if (!apifyClient) {
    initializeApifyClient();
  }

  try {
    // Start the YouTube Transcripts actor
    const run = await apifyClient.actor('junglee/youtube-transcripts').call({
      videoId: videoId,
      maxWaitSecs: 60
    });

    // Get the dataset items
    const { items } = await apifyClient.dataset(run.defaultDatasetId).listItems();

    if (!items || items.length === 0) {
      throw new Error('No transcript found via Apify');
    }

    const transcript = items[0];

    if (!transcript.transcript || transcript.transcript.length === 0) {
      throw new Error('Empty transcript returned from Apify');
    }

    // Format the transcript
    return {
      text: formatApifyTranscript(transcript),
      hasTranscript: true,
      segments: transcript.transcript,
      language: {
        code: transcript.language || 'unknown',
        name: transcript.language || 'Unknown',
        detected: null
      }
    };
  } catch (error) {
    console.error('Apify API error:', error);
    throw new Error(`Apify transcript extraction failed: ${error.message}`);
  }
}

function formatApifyTranscript(transcript) {
  if (!transcript.transcript) return '';

  return transcript.transcript
    .map(segment => `[${formatTimestamp(segment.offset / 1000)}] ${segment.text}`)
    .join('\n');
}

function formatTimestamp(seconds) {
  const minutes = Math.floor(seconds / 60);
  const remainingSeconds = Math.floor(seconds % 60);
  return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
}

module.exports = {
  initializeApifyClient,
  getTranscriptViaApify
};
